import './App.css';
import api from "./api"
import Button from 'react-bootstrap/Button'
import React from "react"

function Utils() {

  const devices = []

  function buttonDiscoveryClick() {
    api.getNetworkDiscover(function (res) {
      console.log(JSON.stringify(res))
      res.forEach(deviceRes => {
        console.log(deviceRes.data.ipaddr)
        const device = {
          hostname: deviceRes.data.hostname,
          ifname: deviceRes.data.ifname,
          macaddr: deviceRes.data.macaddr,
          name: deviceRes.data.name,
          nerves: deviceRes.data.nerves,
          version: deviceRes.data.variant,
          ipaddr: deviceRes.data.ifname
        }
        devices.push(device)
      });

      console.log(devices)

    })
  }

  return (
    <>
      <Button variant="primary" onClick={buttonDiscoveryClick}>
        Discovery
      </Button>


    </>
  )
}

export default Utils;
