import { useState } from 'react'

import './App.css';
import api from "./api"
import Button from 'react-bootstrap/Button'
import Table from 'react-bootstrap/Table'
import React from "react"

function Utils() {

  const [devices, setDevices] = useState([])

  function buttonDiscoveryClick() {
    api.getNetworkDiscover(function (res) {
      console.log(JSON.stringify(res))
      setDevices(res)
    })
  }
  [{
    "name": "nerves", "action": "id",
    "data": {
      "hostname": "nerves-4199", "ifname": "eth0", "macaddr": "A8E77DED22E8",
      "name": "nerves", "version": "0.1.2", "ipaddr": "192.168.1.79"
    }
  }]
  const rows = devices.map(d => <tr key="d.data.macaddr">
    <td>{d.data.macaddr}</td>
  </tr>)

  return (
    <>
      <Button variant="primary" onClick={buttonDiscoveryClick}>
        Discovery
      </Button>
      <Table striped bordered hover variant="dark">
        <thead>
          <tr>
            <th>#</th>
            <th>Hostname</th>
            <th>Ifname</th>
            <th>MAC Address</th>
            <th>Name</th>
            <th>Version</th>
            <th>IP Address</th>

          </tr>
        </thead>
        <tbody>
          {rows}
        </tbody>
      </Table>

    </>
  )

}

export default Utils;
