import { useState } from 'react'

import './App.css';
import api from "./api"
import Button from 'react-bootstrap/Button'
import Table from 'react-bootstrap/Table'
import React from "react"

function Utils() {

  const [devices, setDevices] = useState({
    hostname: "",
    ifname: "",
    macaddr: "",
    name: "",
    nerves: "",
    version: "",
    ipaddr: ""
  })


  function buttonDiscoveryClick() {
    api.getNetworkDiscover(function (res) {
      console.log(JSON.stringify(res))
      res.forEach(deviceRes => {
        console.log(deviceRes.data.ipaddr)
        const device = {
          hostname: deviceRes.data.hostname,
          ifname: deviceRes.data.ifname,
          macaddr: deviceRes.data.macaddr,
          name: deviceRes.data.name,
          nerves: deviceRes.data.nerves,
          version: deviceRes.data.variant,
          ipaddr: deviceRes.data.ipaddr
        }
        devices.push(device)
      });
      console.log(`Es mi array interno ${JSON.stringify(devices[0])}`)
    })
  }

  function renderPerson(person, index) {
    return (
      <tr key={0}>
        <td>{person.name}</td>
        <td>{person.address}</td>
        <td>{person.age}</td>
      </tr>
    )
  }

  return (
    <>
      <Button variant="primary" onClick={buttonDiscoveryClick}>
        Discovery
      </Button>

      <Table striped condensed hover>
        <thead>
          <tr>
            <th></th>
            <th>Name</th>
            <th>Address</th>
            <th>Age</th>
          </tr>
        </thead>
        <tbody>

        </tbody>
      </Table>

    </>
  )

}

export default Utils;
